import React from 'react';
import PropTypes from 'prop-types';
import classNames from 'classnames';

class Radio extends React.Component {
  static propTypes = {
    children: PropTypes.node.isRequired,
    className: PropTypes.string,
    labelName: PropTypes.string
  }

  static defaultProps = {
    labelName: 'Label'
  }

  render() {
    return (
      <div className='radio'>
        <div className='radio-label'>{ this.props.labelName }</div>
        { this.props.children }
      </div>
    )
  }
}

Radio.Option = class Option extends Component {
  static propTypes = {
    children: PropTypes.node,
    value: PropTypes.string,
    className: PropTypes.string,
    checked: PropTypes.bool
  }

  render() {
    return (
      <label
        className='radio-option'
        htmlFor={ this.props.value }
        >
        <input
          type='radio'
          className='radio-option-input'
          name={ this.props.inputName }
          value={ this.props.value }
          id={ this.props.value }
          defaultChecked={ this.props.checked }
          />
        <span className='radio-option-label'>
          <div>{ this.props.value }</div>
          <div className='text-gray'>{ this.props.children }</div>
        </span>
      </label>
    )
  }

}

export default Radio;
