import Form from '../components/form';
import Input from '../components/input';
import Radio from '../components/radio';
import Select from '../components/select';
import Button from '../components/button';

class Home extends React.Component {
  render() {
    return (
      <div className='container'>
        <div className=''>
          <h1>RSVP</h1>
          <Form>
            <Input labelName='Full Name' />
            <Radio labelName='Attending'>
              <Radio.Option value='Happily accepts' inputName='attendance' />
              <Radio.Option value='Regretfully declines' inputName='attendance'/>
            </Radio>
            <Select labelName='Number of guests attending'>
              <Select.Option>0</Select.Option>
              <Select.Option>1</Select.Option>
              <Select.Option>2</Select.Option>
              <Select.Option>3</Select.Option>
              <Select.Option>4</Select.Option>
              <Select.Option>5</Select.Option>
              <Select.Option>6</Select.Option>
            </Select>
            <Radio labelName=''>
              <Radio.Option value='Happily accepts' inputName='attendance' />
              <Radio.Option value='Regretfully declines' inputName='attendance'/>
            </Radio>
            <Button cta='Submit' />
          </Form>
        </div>
        <style jsx global>{`
          body {
            margin: 0;
            padding: 0;
            height: 100vh;
            font-size: 16px;
          }
          .container {
            height: 100vh;
            display: flex;
            justify-content: center;
            align-items: center;
          }
          .hide {
            display: none;
          }
          .flex {
            display: flex;
          }
        `}</style>
      </div>
    )
  }

}

export default Home
