import PropTypes from 'prop-types';
import classNames from 'classnames';

import Block from '../components/block';
import List from '../components/list';
import Link from '../components/link';

class Details extends React.Component {
  renderTravel() {
    return (
      <div className='details-travel'>
        <h4>Travel</h4>
        <Block blockTitle='Accomodations'>
          If you're coming in from out of town, we recommend getting an Airbnb in Mill Valley or San Francisco. There's plenty of beautiful options around the immediate area.
          <List>
            <List.Item><Link linkTo=''>Mill Valley Inn</Link></List.Item>
            <List.Item><Link linkTo=''>Airbnb</Link></List.Item>
          </List>
        </Block>
        <Block blockTitle='Parking & Transportation'>
          The Outdoor Art Club doesn't have public parking but there is a lot one block away.
          <List>
            <List.Item><Link linkTo=''>Parking Lot</Link></List.Item>
          </List>
        </Block>
        <style jsx>{`
          .details-travel {
            margin-bottom: 48px;
          }
          .details-travel h4 {
            margin-bottom: 24px;
          }
        `}</style>
      </div>
    )
  }

  renderRegistry() {
    return (
      <div className='details-registry'>
        <h4>Registry</h4>
        <Block blockTitle='Online'>
          This is entirely optional. If you do pick something from the registry, you're welcome to bring it in person or send it directly.
          <List>
            <List.Item><Link linkTo=''>Zola</Link></List.Item>
          </List>
        </Block>
        <style jsx>{`
          .details-registry h4 {
            margin-bottom: 24px;
          }
        `}</style>
      </div>
    )
  }
  render() {
    return (
      <div className='details'>
        { this.renderTravel() }
        { this.renderRegistry() }
        <style jsx>{`
          .details {
            grid-area: details;
          }
        `}</style>
      </div>
    )
  }

}

export default Details
