import Form from '../components/form';
import Input from '../components/input';
import Radio from '../components/radio';
import Checkbox from '../components/checkbox';
import Button from '../components/button';

const encode = (data) => {
  return Object.keys(data)
    .map(key => encodeURIComponent(key) + "=" + encodeURIComponent(data[key]))
    .join("&");
}

class Home extends React.Component {
  constructor(props) {
    super(props);
    this.state = { name: "", attendance: "", guests: "", preferences: "" };
  }

  handleSubmit = e => {
    fetch("/", {
      method: "POST",
      headers: { "Content-Type": "application/x-www-form-urlencoded" },
      body: encode({ "form-name": "rsvp", ...this.state })
    })
      .then(() => alert("Success!"))
      .catch(error => alert(error));

    e.preventDefault();
  };

  handleChange = e => this.setState({ [e.target.name]: e.target.value });

  render() {
    const { name, attendance, guests, preferences } = this.state;

    return (
      <div className='container'>
        <div>
          <h1>RSVP</h1>
          <Form submit={ this.handleSubmit }>
            <Input inputName='name' labelName='Full Name' placeholder='Full Name' />
            <Radio inputName='attendance' labelName='RSVP'>
              <Radio.Option value='Happily accepting' inputName='attendance' />
              <Radio.Option value='Regretfully declining' inputName='attendance'/>
            </Radio>
            <Input inputName='guests' labelName='Number of Guests Attending' placeholder='0' />
            <Input inputName='preferences' labelName='Number of Vegetarians / Vegans' placeholder='0' />
            <Button cta='Submit' />
          </Form>
        </div>
        <style jsx global>{`
          @import url('https://fonts.googleapis.com/css?family=Lexend+Deca&display=swap');
          body {
            font-family: 'Lexend Deca', sans-serif;
            font-size: 16px;
            margin: 0;
            padding: 0;
            height: 100vh;
          }
          .container {
            min-height: 100vh;
            display: flex;
            justify-content: center;
            align-items: center;
          }
        `}</style>
      </div>
    )
  }

}

export default Home
