import PropTypes from 'prop-types';
import classNames from 'classnames';

class Select extends React.Component {
  static propTypes = {
    children: PropTypes.node.isRequired,
    className: PropTypes.string,
    labelName: PropTypes.string,
    defaultValue: PropTypes.string
  }

  static defaultProps = {
    defaultValue: 'Select',
    labelName: 'Label'
  }

  render() {
    return (
      <div className='select'>
        <div className='select-label'>{ this.props.labelName }</div>
        <select className='select-option' defaultValue={ this.props.defaultValue }>
          <option disabled value='Select'>Select</option>
          { this.props.children }
        </select>
        <style jsx>{`
          .select-option {
            display: flex;
            flex-direction: column;
            margin-bottom: 16px;
            flex: 1;
          }
          .input-label {
            font-size: inherit;
            margin-bottom: 8px;
          }
          .input-text {
            height: 50px;
            font-size: inherit;
            padding: 0 16px;
          }
          .input-text:focus {
            outline: none;
          }
        `}</style>
      </div>
    )
  }
}

Select.Option = class Option extends React.Component {
  static propTypes = {
    children: PropTypes.node.isRequired
  }

  render() {
    return (
      <option className='select-option-input' value={ this.props.children }>
        { this.props.children }
      </option>
    )
  }

}

export default Select;
